import 'package:flutter/material.dart';
import 'package:flutter_bloc/flutter_bloc.dart';
import 'package:widgets/bloc/card_bloc_bloc.dart';


void main (){
  runApp(mac7());
}

class mac7 extends StatefulWidget {
  const mac7({super.key});

  @override
  State<mac7> createState() => _mac7State();
}

class _mac7State extends State<mac7> {
  @override
  Widget build(BuildContext context) {
    return  Scaffold(
      appBar: AppBar(backgroundColor: Colors.red, title: Text('My App'), 
      elevation: 0,
      actions: [
        BlocBuilder<CardBloc, CardBlocState>(
          builder: (context, state) {
            if (state is CardblocValueChangedState) {
              return Badge.count(
                count: BlocProvider.of<CardBloc>(context).counter,
                child: IconButton(
                  onPressed: () {},
                  icon: Icon(Icons.shopping_cart),
                ),
              );
              //Text(state.counter.toString());
            } else {
              return SizedBox();
            }
          },
        ),
      ]),
      body: Column(
        //mainAxisAlignment: MainAxisAlignment.center,
        crossAxisAlignment: CrossAxisAlignment.center,
        children: [
          Image(
            image:NetworkImage("data:image/jpeg;base64,"),
            height: 200,
            fit: BoxFit.cover,
          ),
         // Text(""),
          Text(
            "MacBook Air512 3لابتوب ماك بوك اير (15-انج \ام جيجا بايت  ,)",
            overflow: TextOverflow.clip,
maxLines: 3,
          ),
          Text("2,235,000"),
          ElevatedButton(
             style: ElevatedButton.styleFrom(
    backgroundColor: Colors.red, 
    foregroundColor: Colors.white, 
  ),
              onPressed: () {
                BlocProvider.of<CardBloc>(context).add(IncrementEvent());
                ScaffoldMessenger.of(context)
                    .showSnackBar(SnackBar(content: Text('Added to cart'),
                    backgroundColor: Colors.red,
                    ));
              },
              child: Text("Add to cart"))
        ],
      ),
    );
  }
}